import React, { useReducer, useEffect } from "react";
import Home from "./pages/Home";
import MyBooks from "./pages/MyBooks";
import Search from "./components/Search";
import Results from "./pages/Results";
import Nav from "./components/Nav";
import axios from "axios";
// import spinner from "../images/ajax-loader.gif";
import { initialState, reducer } from "./store/reducer";
import {
  BrowserRouter as Router,
  Route,
  Switch,
} from "react-router-dom";

const BOOK_API = "https://www.googleapis.com/books/v1/volumes?q=";


const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    axios.get(BOOK_API).then(jsonResponse => {
      dispatch({
        type: "SEARCH_BOOKS_SUCCESS",
        payload: jsonResponse.data.Search
      });
    });
  }, []);

  searchBooks = async text => {
    console.log("searchBooks method", text);

      axios(`https://www.googleapis.com/books/v1/volumes?q=${text}`).then(
  jsonResponse => {
  if (jsonResponse.data.Response === "True") {
    dispatch({
      type: "SEARCH_BOOKS_SUCCESS",
      payload: jsonResponse.data.Search
    });
  } else {
    dispatch({
      type: "SEARCH_BOOKS_FAILURE",
      error: jsonResponse.data.Error
    });
   }
  }
 );
};


  return (
    <div>
      <Nav />
      <Router>
        <Switch>
          <Route exact path={["/" ]}>
            <Home />
          </Route>
          <Search searchBooks={this.searchBooks} />
        {this.state.search ? <Books books={this.state.books} /> : <Saved />}
          <Route exact path={["/books/"]}>
            <MyBooks />
          </Route>
          <Route exact path={["/books/:id"]}>
            <Results />
          </Route>
        </Switch>
      </Router>

    </div>
  );
}

export default App;
