import React, { useState, useEffect } from "react";
import { Col, Row, Container } from "../components/Grid";
import { Input, FormBtn } from "../components/Form";
import API from "../utils/API";
import "./style.css";
import { List, ListItem } from "../components/List";
import DeleteBtn from "../components/DeleteBtn";
import Card from "../componenets/Card";
import { Link } from "react-router-dom";


function Home() {
  const [books, setBooks] = useState([])
  const [formObject, setFormObject] = useState({})

  useEffect(() => {
    loadBooks()
  }, [])

  function loadBooks() {
    API.getBooks()
      .then(res => 
        setBooks(res.data)
      )
      .catch(err => console.log(err));
  };


  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };
  function deleteBook(id) {
    API.deleteBook(id)
      .then(res => loadBooks())
      .catch(err => console.log(err));
  }


  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveBook({
        title: formObject.title,
        author: formObject.author,
      })
        .then(res => loadBooks())
        .catch(err => console.log(err));
    }
  };



    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <div  className ="container">
            <form className ="form">
              <Input name="title" placeholder="Title " value={book.title} onChange={handleInputChange} />
              <Input name="author" placeholder="Author " value={book.author} onChange={handleInputChange}/>
              <FormBtn  onClick={handleFormSubmit}>Search Book</FormBtn>
            </form>
            </div>
            {books.length ? (
            <Card>
                {books.map(book => (
                  <ListItem key={book._id}>
                    <Link to={"/books/" + book._id}>
                      <strong>
                        {book.title} by {book.author}
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => deleteBook(book._id)} />
                  </ListItem>
                ))}
              </Card>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col> 
        </Row>
      </Container>
    );
  };


  export default Home;
