import React, { useReducer, useEffect } from "react";
import Books from "./pages/Books";
import Nav from "./components/Nav";
import axios from "axios";
// import spinner from "../images/ajax-loader.gif";
import { initialState, reducer } from "./store/reducer";


const BOOK_API = "https://www.googleapis.com/books/v1/volumes?q=";


const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    axios.get(BOOK_API).then(jsonResponse => {
      dispatch({
        type: "SEARCH_BOOKS_SUCCESS",
        payload: jsonResponse.data.Search
      });
    });
  }, []);

  const search = query => {
    dispatch({
      type: "SEARCH_BOOKS_REQUEST"
    });

      axios(`https://www.googleapis.com/books/v1/volumes?q=${query}`).then(
  jsonResponse => {
  if (jsonResponse.data.Response === "True") {
    dispatch({
      type: "SEARCH_BOOKS_SUCCESS",
      payload: jsonResponse.data.Search
    });
  } else {
    dispatch({
      type: "SEARCH_BOOKS_FAILURE",
      error: jsonResponse.data.Error
    });
   }
  }
 );
};

const { books, errorMessage, loading} = state;

const retrievedBooks =
loading && !errorMessage ? (
{/* <img className="spinner" src={spinner} alt="Loading spinner" /> */}
) : errorMessage ? (
  <div className="errorMessage">{errorMessage}</div>
) : (
  books.map((book, index) => (
    <Books key={`${index}-${book.Title}`} book={book} />
    ))
  );

  return (
    <div>
      <Nav />
      <Router>
        <Switch>
          <Route exact path={["/", "/books"]}>
            <Books />
          </Route>

        </Switch>
      </Router>

    </div>
  );
}

export default App;
