import React, { useState, useEffect } from "react";
import Jumbotron from "../components/Jumbotron";
import DeleteBtn from "../components/DeleteBtn";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import API from "../utils/API"


function Books() {
  // Initialize books as an empty array
    const [books, setBooks] = useState([]);
    const [ bookObject, setBookObject ] = useState({title: "", author: "", synopsis: ""})

    useEffect(() => {
      loadBooks();

    }, []);

  const submitBook = (e) => {
    e.preventDefault()
    API.saveBook(bookObject).then ( resp => {
    const booksCopy = books.slice(0)
    booksCopy.push(resp.data)
    setBooks(booksCopy)
  })
  }

  function deleteBook(e, id) {
    console.log( e.target.getAttribute("dataid"))
    e.preventDefault()
    API.deleteBook(id).then (resp => {
      loadBooks()
  })
  }

const handleInputChange = (e) => {
  e.preventDefault()
  setBookObject({...bookObject, [e.target.name]: e.target.value})
}

    function loadBooks() {
      // Add code here to get all books from the database and store them using setBooks

      API.getBooks().then( resp => {
        console.log(resp)
        setBooks(resp.data)
      })
    }

    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>What Books Should I Read?</h1>
            </Jumbotron>
            <form>
              <Input name="title" placeholder="Title (required)" value={bookObject.title} onChange={handleInputChange} />
              <Input name="author" placeholder="Author (required)" value={bookObject.author} onChange={handleInputChange}/>
              <TextArea name="synopsis" placeholder="Synopsis (Optional)" value={bookObject.synopsis} onChange={handleInputChange}/>
              <FormBtn  onClick={submitBook}>Submit Book</FormBtn>
            </form>
          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Books On My List</h1>
            </Jumbotron>
            {books.length ? (
              <List>
                {books.map(book => (
                  <ListItem key={book._id}>
                    <a href={"/books/" + book._id}>
                      <strong>
                        {book.title} by {book.author}
                      </strong>
                    </a>
                    <DeleteBtn book={book} onClick={deleteBook}/>
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }

export default Books;
