import React, { useState, useEffect } from "react";
import DeleteBtn from "../components/DeleteBtn";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, FormBtn } from "../components/Form";
import API from "../utils/API";
import "./style.css";




function Books() {
  // Initialize books as an empty array
    const [books, setBooks] = useState([]);
    const [ bookObject, setBookObject ] = useState({title: "", author: "", description: "", image: "", link: ""})

    useEffect(() => {
      loadBooks();

    }, []);

  const submitBook = (e) => {
    e.preventDefault()
    API.saveBook(bookObject).then ( resp => {
    const booksCopy = books.slice(0)
    booksCopy.push(resp.data)
    setBooks(booksCopy)
  })
  }

  function deleteBook(e) {
    const id =e.target.getAttribute("dataid")

    API.deleteBook(id).then (resp => {
      loadBooks()
  })
  }

const handleInputChange = (e) => {
  e.preventDefault()
  setBookObject({...bookObject, [e.target.name]: e.target.value})
}

    function loadBooks() {
      // Add code here to get all books from the database and store them using setBooks

      API.getGoogleBooks().then( resp => {
        console.log(resp)
        setBooks(resp.data)
      })
    }

    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <div  className ="container">
            <form className ="form">
              <Input name="title" placeholder="Title " value={bookObject.title} onChange={handleInputChange} />
              <Input name="author" placeholder="Author " value={bookObject.author} onChange={handleInputChange}/>
              <FormBtn  onClick={submitBook}>Search Book</FormBtn>
            </form>
            </div>
          </Col> 
        </Row>
      </Container>
    );
  }

export default Books;
